INSERT INTO wp_nwm__wsd_plugin_alerts VALUES("19","0","0","check_index_wp_uploads","The <strong>\"index.php\"</strong> file was found in the <strong>\"/wp-content/uploads\"</strong> directory","<p>A directory listing provides an attacker with the complete index of all the resources located inside of the directory. The specific risks and consequences vary depending on which files are listed and accessible.</p>
